INSERT INTO `zrjx_posts` VALUES ('1', '1', '关于我们', null, '2018-03-02 20:54:19', '<div class=\"ab_con clearfix\"><div class=\"ab_con_r\"><p class=\"ab_con_l\"><img src=\"/data/upload/ueditor/20180313/5aa6b6724c2dc.jpg\" title=\"201505181749265559b5a6b3bfb (1).jpg\" alt=\"201505181749265559b5a6b3bfb (1).jpg\"/></p><div class=\"ab_con_r_tit\"><h3><img src=\"/data/upload/ueditor/20180313/5aa6b660ad1a2.png\" title=\"201505181749345559b5ae415ad.png\" alt=\"201505181749345559b5ae415ad.png\"/></h3><p><span style=\"font-size: 14px;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">企业概况</span></strong></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;\"></span></p><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">福州振荣机械输送设备有限公司专业生产各种固定式输送机、移动式输送机、船上半自动式、自动式卸砂输送机、各类工业用生产流水线及各种非标输送设备等。</span></p><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span></p><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">创建于1998年，主要员工由原福州输送机厂技术骨干力量组成，属于福州市中大型输送设备私营企业。厂房面积<span style=\"font-size: 14px; font-family: Calibri;\">3000</span>平米。公司拥有近<span style=\"font-size: 14px; font-family: Calibri;\">200</span>人高素质员工队伍，在职专业技术人员<span style=\"font-size: 14px; font-family: Calibri;\">11</span>人中大专以上学历<span style=\"font-size: 14px; font-family: Calibri;\">8</span>人，工厂拥有各种型号的普通机械制造设备和与众不同规格的数控加工设备，从铸造、锻造到冷作加工、切削加工、热处理表面处理，以及塑料、胶木制造等技术开发能力。形成一条龙服务网络体系。</span></p></div></div></div><div class=\"tit_ad\"><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">本公司与各地的工矿企业、码头、钢铁、煤碳、化工、建材、食品、船上、河砂矿等都有业务往来，产品遍布省内外，并深得用户好评和认可。公司以一流服务获得广大客户赞颂，并多次得到相关行政机关的表彰和奖励。</span></p></div>', '关于我们', '福州振荣机械输送设备有限公司专业生产各种固定式输送机、移动式输送机、船上半自动式、自动式卸砂输送机、各类工业用生产流水线及各种非标输送设备等。\r\n\r\n\r\n创建于1998年，主要员工由原福州输送机厂技术骨干力量组成，属于福州市中大型输送设备私营企业。厂房面积3000平米。公司拥有近200人高素质员工队伍，在职专业技术人员11人中大专以上学历8人，工厂拥有各种型号的普通机械制造设备和与众不同规格的数控加工设备，从铸造、锻造到冷作加工、切削加工、热处理表面处理，以及塑料、胶木制造等技术开发能力。形成一条龙服务网络体系。', '1', '1', '2018-03-02 20:53:58', null, '0', '2', '', '0', '{\"template\":\"about\",\"thumb\":\"portal\\/20180313\\/5aa6b69ced931.jpg\"}', '0', '0', '0', '0');
